.btn {
  @apply px-4 py-2 rounded-md font-medium transition-colors;
  @apply focus:outline-none focus:ring-2 focus:ring-offset-2;
}

.btn-primary {
  @apply bg-botnar-pink-500 text-white;
  @apply hover:bg-botnar-pink-600;
  @apply focus:ring-botnar-pink-500;
}

.data-card {
  @apply bg-white rounded-lg border border-gray-200 p-6 shadow-sm;
}

.data-card-header {
  @apply flex items-center justify-between mb-2;
}

.data-card-title {
  @apply text-sm font-medium text-gray-600;
}

.data-card-value {
  @apply text-2xl font-bold text-gray-900 mb-1;
}

.data-card-change {
  @apply text-sm;
}

.data-card-change.positive {
  @apply text-green-600;
}

.data-card-change.negative {
  @apply text-red-600;
}

