// Alpine.js component
function dataCard() {
  return {
    value: 0,
    change: 0,
    isPositive: true,

    init() {
      this.isPositive = this.change >= 0;
    },

    formatValue(value) {
      return new Intl.NumberFormat().format(value);
    },

    formatChange(change) {
      const sign = change >= 0 ? '+' : '';
      return `${sign}${change}%`;
    }
  }
}
